package com.ski.box.view.view.dialog;import android.content.Context;import android.content.DialogInterface;import android.view.View;import android.widget.TextView;import androidx.annotation.NonNull;import com.hwangjr.rxbus.RxBus;import com.ski.box.ConstantValue;import com.ski.box.R;import com.ski.box.bean.DataCenter;import com.ski.box.bean.MkBetParamEntity;import com.ski.box.view.activity.LoginActivity;import com.ski.box.view.view.DoubleBetDailogDView;import com.yb.core.utils.LanguageUtil;import com.yb.core.utils.ScreenUtils;import com.yb.core.utils.ToastUtil;import org.jetbrains.annotations.NotNull;import java.util.List;import static com.ski.box.ConstantValue.EVENT_REQUEST_BET_SUBMIT;public class LanguageSwitchDialog extends StrongBottomSheetDialog {    private final TextView mTvZh;    private final TextView mTvVi;    private final Context mContext;    private OnLanguageSwitchListener mOnLanguageSwitchListener;    public LanguageSwitchDialog(@NonNull @NotNull Context context) {        super(context);        mContext = context;        setContentView(R.layout.ski_language_switch_view);        mTvZh = findViewById(R.id.tv_zh);        mTvVi = findViewById(R.id.tv_vi);    }    public void setOnClickListener(OnLanguageSwitchListener onLanguageSwitchListener) {        mOnLanguageSwitchListener = onLanguageSwitchListener;        LSCLickListener lSCLickListener = new LSCLickListener();        mTvZh.setOnClickListener(lSCLickListener);        mTvVi.setOnClickListener(lSCLickListener);    }    public class LSCLickListener implements View.OnClickListener {        @Override        public void onClick(View view) {            int id = view.getId();            if(id == R.id.tv_zh) {                LanguageUtil.languageSwitch(mContext, LanguageUtil.ZH);                mOnLanguageSwitchListener.languageSwitch();                dismiss();            } else if(id == R.id.tv_vi) {                LanguageUtil.languageSwitch(mContext, LanguageUtil.VI);                mOnLanguageSwitchListener.languageSwitch();                dismiss();            }        }    }    public interface OnLanguageSwitchListener {        void languageSwitch();    }}